    $image_col = 'col-md-4 col-sm-4';
    $sumary_col = 'col-md-5 col-sm-4';
}
?>
<div id="node-<?php print $node->nid; ?>" class="<?php print $classes; ?> clearfix"<?php print $attributes; ?>>
