/**
 * @file
 * Default simple view template to all the fields as a row.
 *
 * - $view: The view in use.
 * - $fields: an array of $field objects. Each one contains:
 *   - $field->content: The output of the field.
 *   - $field->raw: The raw data for the field, if it exists. This is NOT output safe.
 *   - $field->class: The safe class id to use.
 *   - $field->handler: The Views field handler object controlling this field. Do not use
 *     var_export to dump this object, as it can't handle the recursion.
 *   - $field->inline: Whether or not the field should be inline.
 *   - $field->inline_html: either div or span based on the above flag.
 *   - $field->wrapper_prefix: A complete wrapper containing the inline_html to use.
 *   - $field->wrapper_suffix: The closing tag for the wrapper.
 *   - $field->separator: an optional separator that may appear before a field.
 *   - $field->label: The wrap label text to use.
 *   - $field->label_html: The full HTML of the label to use including
 *     configured element type.
 * - $row: The raw result object from the query, with all data it fetched.
 *
 * @ingroup views_templates
 */
?>
<div class="innerItem">
    <?php if(isset($fields['field_media_multi'])): ?>
        <div class="jvpost-alignleft jvpost-thumb width50"><?php print $fields['field_media_multi']->content; ?></div>
    <?php endif; ?>
    <div class="content-item-desc">
        <?php if(isset($fields['title'])): ?>
            <h4 class="post-title"><?php print $fields['title']->content; ?></h4>
        <?php endif; ?>
        <?php if(isset($fields['created']) || isset($fields['comment_count'])): ?>
            <div class="entry-meta ItemLinks ItemLinksInline ItemLinksTop margin-0">
                <span class="created">
                    <span class="meta-label"><?php print $fields['created']->label ?></span>
                    <?php $dates = array_combine(array('d','m','y'),explode(' ',format_date($fields['created']->content,'custom','d M Y'))); ?>
                    <?php if(isset($dates)){ ?>
                    <span class="d"><?php print $dates['d']; ?></span>
                    <span class="m"><?php print $dates['m'] ?></span>
                    <span class="y"><?php print $dates['y'] ?></span>
                    <?php } ?>
                </span>
                <?php if(isset($fields['comment_count'])): ?>
                    <span class="comment">
                        <span class="meta-label"><?php print $fields['comment_count']->label ?></span>
                        <?php print $fields['comment_count']->content ?>
                    </span>
                <?php endif; ?>
            </div>
        <?php endif; ?>
    </div>
</div>
